import React, { useState, useEffect, useRef } from "react";
import "../styles/todo-app.css";
import Todo from "./Todo";
import TodoForm from "./TodoForm";

const TodoApp = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = (todo) => {
    if (!todo.text || /^\s*$/.test(todo.text)) {
      return;
    }
    const newTodos = [todo, ...todos];
    setTodos(newTodos);
    console.log(todo, ...todos);
  };
  const editTodo = (todoId, editedTodo) => {
    if (!editedTodo.text || /^\s*$/.test(editedTodo.text)) {
      return;
    }
    setTodos((prev) =>
      prev.map((item) => (item.id === todoId ? editedTodo : item))
    );
  };

  const deleteTodo = (id) => {
    const updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    console.log("Deleted Todo");
  };
  const toggleComplete = (id) => {
    let updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };
  console.log(todos);
  const clearTodos = () => {
    setTodos([]);
  };
  return (
    <div className="todo-app">
      <h1>What's the Plan for Today?</h1>
      <TodoForm onSubmit={addTodo} todos={todos} />
      <Todo
        todos={todos}
        toggleComplete={toggleComplete}
        deleteTodo={deleteTodo}
        editTodo={editTodo}
      />
      <div className="clear-button" onClick={clearTodos}>
        Clear To-Do List
      </div>
    </div>
  );
};

export default TodoApp;
