const App = () => {
  //   const inputRef = useRef(null);
  const [todos, setTodos] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setTodos(...todos, { id: todos.length, value: e.target.value });
  };

  console.log(todos);

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="input">
        Add an item to your <u>To-Do</u> list!
        <textarea
          name="input"

          //   ref={inputRef}s
        />
      </label>
      <input type="submit" value="Add To-Do" />
    </form>
  );
};

