import React, { useState, useEffect, useRef } from "react";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");
  const [editTodo, setEditTodo] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [refState, setRefState] = useState("input");
  const [todoCount, setTodoCount] = useState(0);
  const [isEditing, setIsEditing] = useState(false);
  const [todoId, setTodoId] = useState(null);

  useEffect(() => {
    const loadedTodos = JSON.parse(localStorage.getItem("todos"));
    const loadedTodoCounts = JSON.parse(localStorage.getItem("todoId"));
    if (loadedTodos) {
      setTodos(loadedTodos);
      setTodoCount(loadedTodoCounts);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
    localStorage.setItem("todoId", JSON.stringify(todoCount));
  }, [todos, todoCount]);

  const inputRef = useRef(null);
  const editRef = useRef(null);

  useEffect(() => {
    if (refState === "input") {
      inputRef.current.focus();
    } else {
      editRef.current.focus();
    }
  }, [refState]);

  const addTodo = (e) => {
    e.preventDefault();
    const newTodo = {
      id: todoCount,
      text: input,
      completed: false,
    };
    setTodoCount(todoCount + 1);
    setTodos([...todos].concat(newTodo));
    setInput("");
  };

  const deleteTodo = (id) => {
    const updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const toggleComplete = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const submitEdit = (e) => {
    e.preventDefault();
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === todoId) {
        todo.text = editingText;
      }
      return todo;
    });

    setTodos(updatedTodos);
    setEditTodo(null);
    setEditingText("");
    setIsEditing(false);
  };

  const clearTodos = () => {
    setTodos([]);
    setTodoCount(0);
    inputRef.current.focus();
  };

  return (
    <>
      <form onSubmit={isEditing ? submitEdit : addTodo}>
        <label htmlFor="todo-input">
          Add your <u>To-Do</u> item:
        </label>
        <input
          name="todo-input"
          type="text"
          placeholder="Enter Text Here!"
          onChange={(e) => setInput(e.target.value)}
          value={input}
          ref={inputRef}
        />
        <button type={isEditing ? null : "submit"}>Add To-Do</button>

        {todos.map((todo) => (
          <div key={todo.id}>
            {editTodo !== todo.id ? (
              <div>
                {todo.text}
                {todo.id}
                <input
                  type="checkbox"
                  onChange={() => toggleComplete(todo.id)}
                  checked={todo.completed}
                />
                <button
                  onClick={() => {
                    setTodoId(todo.id);
                    setRefState("edit");
                    setIsEditing(true);
                  }}
                >
                  Edit To-Do
                </button>
                <button onClick={() => deleteTodo(todo.id)}>
                  Delete To-Do
                </button>
              </div>
            ) : (
              <div>
                <input
                  type="text"
                  onChange={(e) => setEditingText(e.target.value)}
                  value={editingText}
                  ref={editRef}
                />
                <button
                  type={isEditing ? "submit" : null}
                  onClick={() => {
                    setRefState("input");
                  }}
                >
                  Submit Edit
                </button>
              </div>
            )}
          </div>
        ))}
      </form>
      <button onClick={clearTodos}>Clear Entire To-Do List</button>
    </>
  );
};

export default App;
