import React, { useState, useEffect, useRef } from "react";
import "./TodoApp.css";
import { ImCheckmark } from "react-icons/im";
import { TiEdit } from "react-icons/ti";
import { AiTwotoneDelete } from "react-icons/ai";
import { IoIosCloseCircleOutline } from "react-icons/io";

import Todo from "./Todo";

const TodoApp = () => {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);
  const [todoId, setTodoId] = useState(0);

  const [editTodo, setEditTodo] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [refState, setRefState] = useState("input");

  const inputRef = useRef(null);
  const editRef = useRef(null);

  useEffect(() => {
    console.log("componentDidMount");
    const loadedTodos = JSON.parse(localStorage.getItem("todos"));
    const loadedTodoIds = JSON.parse(localStorage.getItem("todoId"));
    if (loadedTodos) {
      setTodos(loadedTodos);
      setTodoId(loadedTodoIds);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
    localStorage.setItem("todoId", JSON.stringify(todoId));
  }, [todos, todoId]);

  useEffect(() => {
    if (refState === "input") {
      inputRef.current.focus();
    } else {
      editRef.current.focus();
    }
  }, [refState]);

  const addTodo = (e) => {
    e.preventDefault();
    if (!input || /^\s*$/.test(input)) {
      return;
    }
    const newTodo = {
      id: todoId,
      text: input,
      completed: false,
    };
    setTodoId(todoId + 1);
    setTodos([...todos].concat(newTodo));
    setInput("");
    console.log("Added Todo");
  };

  const deleteTodo = (id) => {
    const updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    console.log("Deleted Todo");
  };

  const toggleComplete = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const submitEdit = (e, id) => {
    e.preventDefault();
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.text = editingText;
      }
      return todo;
    });

    setTodos(updatedTodos);
    setEditTodo(null);
    setEditingText("");
  };

  const clearTodos = () => {
    setTodos([]);
    setTodoId(0);
    inputRef.current.focus();
  };

  return (
    <div className="todo-app">
      <div className="app-title">What's the Plan for Today?</div>
      <form onSubmit={addTodo}>
        <div className="add-todo">
          <label htmlFor="todo-input" className="input-label">
            Add your <u>To-Do</u> item:
          </label>
          <input
            className="add-todo-input"
            name="todo-input"
            type="text"
            placeholder="ex: vacuum living room"
            onChange={(e) => setInput(e.target.value)}
            value={input}
            ref={inputRef}
          />
          <button type="submit" className="add-todo-button">
            Add To-Do
          </button>
        </div>
      </form>
      {todos.map((todo) => (
        <Todo todo={todo} />
      ))}
      <button onClick={clearTodos} className="clear-button">
        Clear Entire To-Do List
      </button>
    </div>
  );
};

export default TodoApp;
