import React, { useState, useEffect, useRef } from "react";

const App = () => {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);
  const [todoId, setTodoId] = useState(0);

  const [editTodo, setEditTodo] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [refState, setRefState] = useState("input");

  const inputRef = useRef(null);
  const editRef = useRef(null);

  useEffect(() => {
    console.log("componentDidMount");
    const loadedTodos = JSON.parse(localStorage.getItem("todos"));
    const loadedTodoIds = JSON.parse(localStorage.getItem("todoId"));
    if (loadedTodos) {
      setTodos(loadedTodos);
      setTodoId(loadedTodoIds);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
    localStorage.setItem("todoId", JSON.stringify(todoId));
  }, [todos, todoId]);

  useEffect(() => {
    if (refState === "input") {
      inputRef.current.focus();
    } else {
      editRef.current.focus();
    }
  }, [refState]);

  const addTodo = (e) => {
    e.preventDefault();
    const newTodo = {
      id: todoId,
      text: input,
      completed: false,
    };
    setTodoId(todoId + 1);
    setTodos([...todos].concat(newTodo));
    setInput("");
    console.log("Added Todo");
  };

  const deleteTodo = (id) => {
    const updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
    console.log("Deleted Todo");
  };

  const toggleComplete = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const submitEdit = (e, id) => {
    e.preventDefault();
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.text = editingText;
      }
      return todo;
    });

    setTodos(updatedTodos);
    setEditTodo(null);
    setEditingText("");
  };

  const clearTodos = () => {
    setTodos([]);
    setTodoId(0);
    inputRef.current.focus();
  };

  return (
    <>
      <form onSubmit={addTodo}>
        <label htmlFor="todo-input" className="form-label">
          Add your <u>To-Do</u> item:
        </label>

        <input
          className="form-input"
          name="todo-input"
          type="text"
          placeholder="Enter a To-do Item Here!"
          onChange={(e) => setInput(e.target.value)}
          value={input}
          ref={inputRef}
        />
        <button type="submit" className="form-submit">
          Add To-Do
        </button>
      </form>

      {todos.map((todo) =>
        editTodo !== todo.id ? (
          <div key={todo.id}>
            {todo.text}
            {/* {todo.id} */}
            <input
              type="checkbox"
              onChange={() => toggleComplete(todo.id)}
              checked={todo.completed}
            />
            <button
              type="button"
              onClick={() => {
                setEditTodo(todo.id);
                setRefState("edit");
              }}
            >
              Edit To-Do
            </button>
            <button type="button" onClick={() => deleteTodo(todo.id)}>
              Delete To-Do
            </button>
          </div>
        ) : (
          <form
            key={todo.id}
            onSubmit={(e) => {
              submitEdit(e, todo.id);
              setRefState("input");
            }}
          >
            <input
              type="text"
              onChange={(e) => setEditingText(e.target.value)}
              value={editingText}
              ref={editRef}
            />
            <button type="submit">Submit Edit</button>
          </form>
        )
      )}
      <button onClick={clearTodos}>Clear Entire To-Do List</button>
    </>
  );
};

export default App;
