import React, { useState, useEffect, useRef } from "react";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");
  const [editTodo, setEditTodo] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [refState, setRefState] = useState("input");
  const [todoId, setTodoId] = useState(0);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const loadedTodos = JSON.parse(localStorage.getItem("todos"));
    const loadedTodoIds = JSON.parse(localStorage.getItem("todoId"));
    if (loadedTodos) {
      setTodos(loadedTodos);
      setTodoId(loadedTodoIds);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
    localStorage.setItem("todoId", JSON.stringify(todoId));
  }, [todos, todoId]);

  const inputRef = useRef(null);
  const editRef = useRef(null);

  useEffect(() => {
    if (refState === "input") {
      inputRef.current.focus();
    } else {
      editRef.current.focus();
    }
  }, [refState]);

  const addTodo = (e) => {
    e.preventDefault();
    const newTodo = {
      id: todoId,
      text: input,
      completed: false,
    };
    setTodoId(todoId + 1);
    setTodos([...todos].concat(newTodo));
    setInput("");
  };

  const deleteTodo = (id) => {
    const updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const toggleComplete = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
    console.log(todos);
  };

  const submitEdit = (id) => {
    console.log(inputRef.current);
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.text = editingText;
      }
      return todo;
    });

    setTodos(updatedTodos);
    setEditTodo(null);
    setEditingText("");
    setIsEditing(false);
  };

  return (
    <>
      <form onSubmit={addTodo}>
        <label htmlFor="todo-input">
          Add your <u>To-Do</u> item:
        </label>
        <input
          name="todo-input"
          type="text"
          placeholder="Enter Text Here!"
          onChange={(e) => setInput(e.target.value)}
          value={input}
          ref={inputRef}
        />
        <button type={isEditing ? "" : "submit"}>Add To-Do</button>
      </form>
      {console.log(isEditing)}
      {todos.map((todo) => (
        <div key={todo.id}>
          {editTodo !== todo.id ? (
            <div>
              {todo.text}
              {todo.id}
              <input
                type="checkbox"
                onChange={() => toggleComplete(todo.id)}
                checked={todo.completed}
              />
              <button
                onClick={() => {
                  setEditTodo(todo.id);
                  setRefState("edit");
                  setIsEditing(true);
                }}
              >
                Edit To-Do
              </button>
              <button onClick={() => deleteTodo(todo.id)}>Delete To-Do</button>
            </div>
          ) : (
            <div>
              <input
                type="text"
                onChange={(e) => setEditingText(e.target.value)}
                value={editingText}
                ref={editRef}
              />
              <button
                type={isEditing ? "submit" : ""}
                onClick={() => {
                  submitEdit(todo.id);
                  setRefState("input");
                }}
              >
                Submit Edit
              </button>
            </div>
          )}
        </div>
      ))}
    </>
  );
};

export default App;
