import React, { useState } from "react";

const App = () => {
  const [todos, setTodos] = useState([]);
  const changeHandler = () => {
    console.log(todos);
    setTodos(todos);
  };
  return (
    <form>
      <label>
        Add an item to your <u>to-do</u> list!
        <input type="text" onChange={changeHandler} />
      </label>
      <button type="submit">Add Todo</button>
    </form>
  );
};

export default App;
