import React, { useState } from "react";
import "./TodoApp.css";

const Todo = ({ todo, toggleComplete }) => {
  const [editTodo, setEditTodo] = useState(null);

  return editTodo !== todo.id ? (
    <div className="todo" key={todo.id}>
      <p
        className={todo.completed ? "completed" : null}
        onClick={() => toggleComplete(todo.id)}
      >
        {todo.text}
        {/* {todo.id} */}
      </p>
      <div className="button-options">
        <div
          className="checkbox"
          onClick={() => toggleComplete(todo.id)}
          checked={todo.completed}
        >
          {todo.completed ? (
            <ImCheckmark className="checked" />
          ) : (
            <IoIosCloseCircleOutline className="unchecked" />
          )}
        </div>
        <div
          className="edit-todo-button"
          type="button"
          onClick={() => {
            setEditTodo(todo.id);
            setRefState("edit");
          }}
        >
          <TiEdit />
        </div>
        <div
          className="delete-todo-button"
          type="button"
          onClick={() => deleteTodo(todo.id)}
        >
          <AiTwotoneDelete />
        </div>
      </div>
    </div>
  ) : (
    <form
      key={todo.id}
      onSubmit={(e) => {
        submitEdit(e, todo.id);
        setRefState("input");
      }}
    >
      <div className="todo-edit">
        <input
          className="todo-edit-input"
          placeholder="Type your edit"
          type="text"
          onChange={(e) => setEditingText(e.target.value)}
          value={editingText}
          ref={editRef}
        />
        <button className="todo-edit-button" type="submit">
          Submit Edit
        </button>
      </div>
    </form>
  );
};

export default Todo;
